#!/usr/vin/env python3

dna = ''
width = 80

def nt_counter(dna, width):
	#delete/comment line below for problem 1
	#dna = dna.replace('\n',' ').replace('\r', ' ')
	if len(dna) >= width:
		for i in range(0, len(dna)- width, width):
			print(dna[i:i+width]+'\n')

		return i
	else:
		print(dna[i:])
 
nt_counter(dna, width)
