#create varible storing DNA sequence as a string
DNA_seq = ''
#find the start position of EcoR1
EcoR1_start_location = DNA_seq.find("GAATTC")
print(EcoR1_start_location)
#find the end position of EcoR1
EcoR1_end_location = EcoR1_start_location + 6
print(EcoR1_end_location)
#print results
print(f'EcoR1 startPos: {EcoR1_start_location} endPos: {EcoR1_end_location}')

#opposite strand
DNA_seq_cp = ''
print(DNA_seq_cp)
#make reverse
DNA_seq_rev = DNA_seq_cp[::-1]
print(DNA_seq_rev)
#find the start position of EcoR1
EcoR1_start_location_rev = DNA_seq_rev.find("CTTAAG")
print(EcoR1_start_location_rev)
#find the end position of EcoR1
EcoR1_end_location_rev = EcoR1_start_location_rev + 6
print(EcoR1_end_location_rev)
#print results
print(f'EcoR1 startPos: {EcoR1_start_location_rev} endPos: {EcoR1_end_location_rev}')
