#create a vairable to store a DNA sequence as a string
DNA_seq = ''
DNA_seq_low = DNA_seq.lower()
print(DNA_seq_low)
#count the numer of each nucleotide
count_a = DNA_seq_low.count('a')
count_t = DNA_seq_low.count('t')
count_g = DNA_seq_low.count('g')
count_c = DNA_seq_low.count('c')
# create variables for AT & GC content
AT = count_a + count_t
print(f'the AT count is {AT} bases')
GC = count_g + count_c 
print(f'the GC count is {GC} bases')
# calculate percentages by dividing AT or GC by the length of the sequence
seq_length = len(DNA_seq)
AT_content = (AT/seq_length)*100
GC_content = (GC/seq_length)*100
print(f' the AT content is {AT_content:.2f}%')
print(f' the GC content is {GC_content:.2f}%')

