
#!/usr/bin/env
#create new empty dictionary
nt_count = {}


#get a set of unique characters in our DNA string
dna = ""

unique = set(dna)
print('unique_nt:', unique)

#iterate through each unique nucleotide
for nt in unique:
	#count the number of this unique nt in dna
	count = dna.count(nt)

	#add our count to our dict
	nt_count[nt] = count

#total sequence counts
counts = (nt_count.values())
total_nts = sum(counts)
#print(total_nts)

#get GC content
GC = []
keys_to_extract = ['G', 'C']
for key in keys_to_extract:
	GC.append(nt_count[key])
#print(GC)
GC_sum = sum(GC)
print(f'GC sum is: {GC_sum}')

#final GC content
GC_final = (GC_sum/total_nts) * 100
print(f'GC content: {GC_final}%')

#print final counts
print('nt_count:' , nt_count)
print(type(nt_count))



